create table public.garden (garden_id bigint generated by default as identity, name varchar(255), primary key (garden_id));
create table public.harvest (date_of_harvest date, weight numeric(38,2), amount bigint, harvest_id bigint generated by default as identity, veg_id bigint, primary key (harvest_id));
create table public.invite (garden_id bigint, invite_code varchar(255) not null, primary key (invite_code));
create table public.app_user (user_id bigint generated by default as identity, oauthid varchar(255) unique, primary key (user_id));
create table public.user_garden (garden_id bigint not null, user_id bigint not null, primary key (garden_id, user_id));
create table public.veg (planting_date date, garden_id bigint, veg_id bigint generated by default as identity, name varchar(255), status varchar(255), primary key (veg_id));
